import React, { useState, useEffect } from 'react';

interface ProgressBarProps {
    progress: number;
    label?: string;
}

const ProgressBar: React.FC<ProgressBarProps> = ({ progress, label }) => (
    <div style={{ marginBottom: '10px' }}>
        {label && <div style={{ marginBottom: '5px' }}>{label}</div>}
        <div style={{ 
            width: '100%', 
            backgroundColor: '#e0e0e0', 
            borderRadius: '4px',
            overflow: 'hidden'
        }}>
            <div style={{
                width: `${progress}%`,
                backgroundColor: '#1976d2',
                height: '10px',
                transition: 'width 0.3s ease-in-out'
            }} />
        </div>
        <div style={{ textAlign: 'right', fontSize: '12px' }}>
            {progress.toFixed(0)}%
        </div>
    </div>
);

const SyllabusUploader: React.FC = () => {
    const [files, setFiles] = useState<FileList | null>(null);
    const [message, setMessage] = useState<string>('');
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [calendarType, setCalendarType] = useState<'google' | 'apple'>('google');
    const [isProcessing, setIsProcessing] = useState(false);
    const [progress, setProgress] = useState<string[]>([]);
    const [currentFileProgress, setCurrentFileProgress] = useState(0);
    const [overallProgress, setOverallProgress] = useState(0);

    useEffect(() => {
        fetch('http://localhost:5002/auth/status', {
            credentials: 'include'
        })
        .then(res => res.json())
        .then(data => setIsAuthenticated(data.authenticated))
        .catch(() => setIsAuthenticated(false));
    }, []);

    const addProgressMessage = (msg: string) => {
        setProgress(prev => [...prev, msg]);
    };

    const handleUpload = async () => {
        if (calendarType === 'google' && !isAuthenticated) {
            setMessage('Please login with Google first');
            return;
        }

        if (!files || files.length === 0) {
            setMessage('Please select at least one file');
            return;
        }

        setIsProcessing(true);
        setProgress([]);
        setMessage('');
        setOverallProgress(0);
        setCurrentFileProgress(0);

        for (let i = 0; i < files.length; i++) {
            const file = files[i];
            addProgressMessage(`Processing ${file.name}...`);
            setCurrentFileProgress(0);

            const formData = new FormData();
            formData.append('file', file);
            formData.append('calendar', calendarType);

            try {
                const healthCheck = await fetch('http://localhost:5002/health');
                if (!healthCheck.ok) {
                    throw new Error('Server is not responding');
                }

                const response = await fetch('http://localhost:5002/upload', {
                    method: 'POST',
                    body: formData,
                    credentials: 'include'
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.error || 'Upload failed');
                }

                setCurrentFileProgress(50);

                if (calendarType === 'apple') {
                    const blob = await response.blob();
                    const url = window.URL.createObjectURL(blob);
                    const a = document.createElement('a');
                    a.href = url;
                    a.download = `calendar-events-${file.name}.ics`;
                    document.body.appendChild(a);
                    a.click();
                    window.URL.revokeObjectURL(url);
                    document.body.removeChild(a);
                    addProgressMessage(`Downloaded calendar file for ${file.name}`);
                } else {
                    const data = await response.json();
                    addProgressMessage(`${file.name}: ${data.message || 'Success!'}`);
                }

                setCurrentFileProgress(100);
                setOverallProgress(((i + 1) / files.length) * 100);
            } catch (error) {
                console.error('Upload error:', error);
                addProgressMessage(`Failed to process ${file.name}: ${error.message}`);
            }
        }

        setIsProcessing(false);
        setMessage('All files processed');
    };

    return (
        <div style={{ padding: '20px' }}>
            <div style={{ marginBottom: '20px' }}>
                <label>
                    <input
                        type="radio"
                        value="google"
                        checked={calendarType === 'google'}
                        onChange={(e) => setCalendarType(e.target.value as 'google' | 'apple')}
                        disabled={isProcessing}
                    /> Google Calendar
                </label>
                <label style={{ marginLeft: '10px' }}>
                    <input
                        type="radio"
                        value="apple"
                        checked={calendarType === 'apple'}
                        onChange={(e) => setCalendarType(e.target.value as 'google' | 'apple')}
                        disabled={isProcessing}
                    /> Apple Calendar
                </label>
            </div>

            {files && files.length > 0 && (
                <div style={{ margin: '20px 0' }}>
                    <ProgressBar 
                        progress={overallProgress} 
                        label={`Overall Progress (${files.length} files)`} 
                    />
                    {isProcessing && (
                        <ProgressBar 
                            progress={currentFileProgress} 
                            label={`Current File: ${files[Array.from(files).findIndex((_, i) => 
                                (i / files.length) * 100 > overallProgress)]?.name}`} 
                        />
                    )}
                </div>
            )}

            {calendarType === 'google' && !isAuthenticated ? (
                <button 
                    onClick={() => window.location.href = 'http://localhost:5002/auth/google'}
                    disabled={isProcessing}
                >
                    Sign in with Google
                </button>
            ) : (
                <>
                    <input 
                        type="file" 
                        multiple
                        accept=".pdf"
                        onChange={(e) => setFiles(e.target.files)}
                        disabled={isProcessing}
                    />
                    <button 
                        onClick={handleUpload}
                        disabled={isProcessing}
                    >
                        {isProcessing ? 'Processing...' : 'Upload Syllabi'}
                    </button>
                </>
            )}

            {/* Progress Messages */}
            <div style={{ 
                marginTop: '20px',
                maxHeight: '200px',
                overflowY: 'auto',
                border: '1px solid #e0e0e0',
                borderRadius: '4px',
                padding: '10px'
            }}>
                {progress.map((msg, index) => (
                    <div key={index} style={{ 
                        margin: '5px 0',
                        padding: '5px',
                        backgroundColor: index === progress.length - 1 ? '#f5f5f5' : 'transparent'
                    }}>
                        {msg}
                    </div>
                ))}
            </div>

            {/* Final Message */}
            {message && (
                <p style={{ 
                    marginTop: '20px',
                    fontWeight: 'bold',
                    color: message.includes('error') ? 'red' : 'green'
                }}>
                    {message}
                </p>
            )}
        </div>
    );
};

export default SyllabusUploader;
