import React, { useState, useEffect } from 'react';

const SyllabusUploader: React.FC = () => {
    const [file, setFile] = useState<File | null>(null);
    const [message, setMessage] = useState<string>('');
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Check auth status when component mounts
    useEffect(() => {
        fetch('http://localhost:5002/auth/status', {
            credentials: 'include'
        })
        .then(res => res.json())
        .then(data => setIsAuthenticated(data.authenticated))
        .catch(() => setIsAuthenticated(false));
    }, []);

    const handleGoogleLogin = () => {
        console.log('Redirecting to Google login...');
        window.location.href = 'http://localhost:5002/auth/google';
    };

    const handleUpload = async () => {
        if (!isAuthenticated) {
            setMessage('Please login with Google first');
            return;
        }

        if (!file) {
            setMessage('Please select a file');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('http://localhost:5002/upload', {
                method: 'POST',
                body: formData,
                credentials: 'include'  // Important for cookies
            });

            const data = await response.json();
            setMessage(data.message || 'Success!');
        } catch (error) {
            setMessage('Upload failed. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            {!isAuthenticated ? (
                <button onClick={handleGoogleLogin}>
                    Sign in with Google
                </button>
            ) : (
                <>
                    <input 
                        type="file" 
                        accept=".pdf"
                        onChange={(e) => setFile(e.target.files?.[0] || null)} 
                    />
                    <button onClick={handleUpload}>Upload Syllabus</button>
                </>
            )}
            {message && <p>{message}</p>}
        </div>
    );
};

export default SyllabusUploader;
