import React, { useState, useEffect } from 'react';

interface ProgressBarProps {
    progress: number;
    label?: string;
}

const ProgressBar: React.FC<ProgressBarProps> = ({ progress, label }) => (
    <div style={{ marginBottom: '10px' }}>
        {label && <div style={{ marginBottom: '5px' }}>{label}</div>}
        <div style={{ 
            width: '100%', 
            backgroundColor: '#e0e0e0', 
            borderRadius: '4px',
            overflow: 'hidden'
        }}>
            <div style={{
                width: `${progress}%`,
                backgroundColor: '#1976d2',
                height: '10px',
                transition: 'width 0.3s ease-in-out'
            }} />
        </div>
        <div style={{ textAlign: 'right', fontSize: '12px' }}>
            {progress.toFixed(0)}%
        </div>
    </div>
);

interface ProcessingStage {
    filename: string;
    stage: 'pdf_extraction' | 'ai_processing' | 'calendar_update' | 'complete' | 'error';
    status: string;
}

const StageIndicator: React.FC<{ stage: ProcessingStage }> = ({ stage }) => {
    const getStageEmoji = () => {
        switch (stage.stage) {
            case 'pdf_extraction': return 'ðŸ“„';
            case 'ai_processing': return 'ðŸ¤–';
            case 'calendar_update': return 'ðŸ“…';
            case 'complete': return 'âœ…';
            case 'error': return 'âŒ';
            default: return 'â³';
        }
    };

    const getStageColor = () => {
        switch (stage.stage) {
            case 'complete': return '#4caf50';  // Green
            case 'error': return '#f44336';     // Red
            case 'pdf_extraction': return '#2196f3';  // Blue
            case 'ai_processing': return '#ff9800';   // Orange
            case 'calendar_update': return '#9c27b0';  // Purple
            default: return '#757575';  // Grey
        }
    };

    return (
        <div style={{ 
            padding: '12px',
            margin: '8px 0',
            backgroundColor: '#f5f5f5',
            borderRadius: '4px',
            borderLeft: `4px solid ${getStageColor()}`,
            transition: 'all 0.3s ease'
        }}>
            <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>
                {getStageEmoji()} {stage.filename}
            </div>
            <div style={{ 
                fontSize: '14px', 
                color: '#666',
                paddingLeft: '24px'  // Indent status message
            }}>
                {stage.status}
            </div>
        </div>
    );
};

const SyllabusUploader: React.FC = () => {
    const [files, setFiles] = useState<FileList | null>(null);
    const [message, setMessage] = useState<string>('');
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [calendarType, setCalendarType] = useState<'google' | 'apple'>('google');
    const [isProcessing, setIsProcessing] = useState(false);
    const [progress, setProgress] = useState<string[]>([]);
    const [currentFileProgress, setCurrentFileProgress] = useState(0);
    const [overallProgress, setOverallProgress] = useState(0);
    const [stages, setStages] = useState<ProcessingStage[]>([]);

    useEffect(() => {
        fetch('http://localhost:5002/auth/status', {
            credentials: 'include'
        })
        .then(res => res.json())
        .then(data => setIsAuthenticated(data.authenticated))
        .catch(() => setIsAuthenticated(false));
    }, []);

    const addProgressMessage = (msg: string) => {
        setProgress(prev => [...prev, msg]);
    };

    const handleUpload = async () => {
        if (calendarType === 'google' && !isAuthenticated) {
            setMessage('Please login with Google first');
            return;
        }

        if (!files || files.length === 0) {
            setMessage('Please select at least one file');
            return;
        }

        setIsProcessing(true);
        setProgress([]);
        setMessage('');
        setStages([]);
        setOverallProgress(0);
        setCurrentFileProgress(0);

        const formData = new FormData();
        Array.from(files).forEach(file => {
            formData.append('file', file);
            setStages(prev => [...prev, {
                filename: file.name,
                stage: 'starting',
                status: 'Preparing to process...'
            }]);
        });
        formData.append('calendar', calendarType);

        try {
            const response = await fetch('http://localhost:5002/upload', {
                method: 'POST',
                body: formData,
                credentials: 'include'
            });

            if (calendarType === 'apple') {
                if (!response.ok) {
                    const errorText = await response.text();
                    try {
                        const errorJson = JSON.parse(errorText);
                        throw new Error(errorJson.error || 'Upload failed');
                    } catch {
                        throw new Error('Failed to generate calendar file');
                    }
                }

                const blob = await response.blob();
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = 'calendar-events.ics';
                document.body.appendChild(a);
                a.click();
                window.URL.revokeObjectURL(url);
                document.body.removeChild(a);

                setMessage('Calendar file downloaded successfully!');
                setCurrentFileProgress(100);
                setOverallProgress(100);
            } else {
                const data = await response.json();

                if (!response.ok) {
                    throw new Error(data.error || 'Upload failed');
                }

                if (data.processing_results) {
                    setStages(data.processing_results);
                }

                setMessage(data.message || 'Success!');
                setCurrentFileProgress(100);
                setOverallProgress(100);
            }
        } catch (error) {
            console.error('Upload error:', error);
            setMessage(`Error: ${error instanceof Error ? error.message : 'Unknown error'}`);
            setStages(prev => [...prev, {
                filename: files[0].name,
                stage: 'error',
                status: error instanceof Error ? error.message : 'Unknown error'
            }]);
        } finally {
            setIsProcessing(false);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <div style={{ marginBottom: '20px' }}>
                <label>
                    <input
                        type="radio"
                        value="google"
                        checked={calendarType === 'google'}
                        onChange={(e) => setCalendarType(e.target.value as 'google' | 'apple')}
                        disabled={isProcessing}
                    /> Google Calendar
                </label>
                <label style={{ marginLeft: '10px' }}>
                    <input
                        type="radio"
                        value="apple"
                        checked={calendarType === 'apple'}
                        onChange={(e) => setCalendarType(e.target.value as 'google' | 'apple')}
                        disabled={isProcessing}
                    /> Apple Calendar
                </label>
            </div>

            {files && files.length > 0 && (
                <div style={{ margin: '20px 0' }}>
                    <ProgressBar 
                        progress={overallProgress} 
                        label={`Overall Progress (${files.length} files)`} 
                    />
                    {isProcessing && (
                        <ProgressBar 
                            progress={currentFileProgress} 
                            label={`Current File: ${files[Array.from(files).findIndex((_, i) => 
                                (i / files.length) * 100 > overallProgress)]?.name}`} 
                        />
                    )}
                </div>
            )}

            {calendarType === 'google' && !isAuthenticated ? (
                <button 
                    onClick={() => window.location.href = 'http://localhost:5002/auth/google'}
                    disabled={isProcessing}
                >
                    Sign in with Google
                </button>
            ) : (
                <>
                    <input 
                        type="file" 
                        multiple
                        accept=".pdf"
                        onChange={(e) => setFiles(e.target.files)}
                        disabled={isProcessing}
                    />
                    <button 
                        onClick={handleUpload}
                        disabled={isProcessing}
                    >
                        {isProcessing ? 'Processing...' : 'Upload Syllabi'}
                    </button>
                </>
            )}

            {/* Progress Messages */}
            <div style={{ 
                marginTop: '20px',
                maxHeight: '200px',
                overflowY: 'auto',
                border: '1px solid #e0e0e0',
                borderRadius: '4px',
                padding: '10px'
            }}>
                {progress.map((msg, index) => (
                    <div key={index} style={{ 
                        margin: '5px 0',
                        padding: '5px',
                        backgroundColor: index === progress.length - 1 ? '#f5f5f5' : 'transparent'
                    }}>
                        {msg}
                    </div>
                ))}
            </div>

            {/* Final Message */}
            {message && (
                <p style={{ 
                    marginTop: '20px',
                    fontWeight: 'bold',
                    color: message.includes('error') ? 'red' : 'green'
                }}>
                    {message}
                </p>
            )}

            {/* Processing Stages */}
            {stages.length > 0 && (
                <div style={{ 
                    marginTop: '20px',
                    maxHeight: '300px',
                    overflowY: 'auto',
                    padding: '10px',
                    border: '1px solid #e0e0e0',
                    borderRadius: '4px'
                }}>
                    <h3>Processing Stages</h3>
                    {stages.map((stage, index) => (
                        <StageIndicator key={index} stage={stage} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default SyllabusUploader;
